        fun filterTextFromSymbols(inputText: String): String {
            val filteredText = inputText.replace(Regex("[^\\p{L}\\p{N}]"), " ")
            val trimmedText = filteredText.replace(Regex("\\s+"), " ")
            return trimmedText
