    fun smartAnswerYesNo(userInput: String): Boolean {
        val lowerInput = userInput.lowercase()

        if (timeRelatedWords.any { lowerInput.contains(it) } || timeRelatedPhrases.any { lowerInput.contains(it) }) {
            return true
        }

        // نظرًا لعدم وجود مكتبة مطابقة ضبابية مباشرة في Kotlin، يمكننا تجاهل هذا الجزء أو تنفيذ منطق بسيط للمطابقة
        // لتبسيط الأمور، سنتجاهل البحث الضبابي هنا

        return false
    }

    fun getReqDate(text: String): String {
        if (!smartAnswerYesNo(text)) return ""
        val formattedDateTime =  getCurrentDateTime()
        return "$formattedDateTime هذا هو التاريخ الحالي والوقت واليوم لا تذكره الا عندما يطلبة منك المستخدم "
    }
    val simaphor = Semaphore(1)
