    var    isbisy=false
    fun getTime(){
        try {
             GlobalScope.async {
                isbisy=true
                val response = SessionChatControl(context).getTime()
                if (response != null) {
                    valueCurrentDateAll = response.description
                }
                isbisy=false
            }
        }catch (E:Exception){ }
        finally { }
    }
    var valueCurrentDateAll=""
    fun getCurrentDateTime(): String {
        // أسماء الأشهر الهجرية
        val currentDate:String
//        val hijrahChronology = HijrahChronology.INSTANCE
        // التاريخ الميلادي
        indexRequest%=10
        if(indexRequest==0) {
            getTime()
//            simaphor.acquire()
        }

        val time = Calendar.getInstance().time
         currentDate = formatter.format(time)?.toString()!!
        indexRequest++

        return  valueCurrentDateAll+" ( الوقت الحالي هو :"+ currentDate +"("
    }
    private fun initializeModel() {
        model = GenerativeModel(
