    var isLoad=false
    suspend fun askAI(text: String,language:String): String {

        if(!isLoad || counterErrors==5) {
            Log.e("isLoad555","isLoad")
            load()
        }

        chat?.let {

            val sgt=  getReqDate(text)
            val invoLanguage=req[language.substring(0,5)]
            Log.d("invoLanguage",language.substring(0,5))
            var notfy="يجب ان تكون ردودك مختصرة ومرتبة  وبلغة منتظمة ولا تزيد عن سطرين  والاجابة تكون بحدود السؤال فقط "
            val response = it.sendMessage("$sgt $text  $invoLanguage $notfy")
            if(response?.text?.isNullOrEmpty()!!) {
                counterErrors++
                return ""
            }
            Log.d("response.text",response?.text!!)
            counterErrors=0
            return response?.text!!
        }
        return ""
    }
