    suspend fun askAndResponseAnswar(inputText:String?,lang:String, callBack: IGeminiServiceEventListener) {

        try {
            var resAnsware = askAI(inputText!!,lang)
            if (resAnsware?.isNullOrEmpty()==false) {
                //TODO Delete non-text symbols
                resAnsware = filterTextFromSymbols(resAnsware)
                //-------------------------------------------
                //  resAnsware=Helper.removeStars(resAnsware)
                //   Log.d("messageToGeneratorAudio3", lang.toString())

                if(resAnsware?.trim()?.isNullOrEmpty()==true)
                    callBack.onRequestIsFailure("empty")
                else {
                    val response = getUrlAudio(resAnsware!!, lang!!)
                    Log.d("response55", response!!)
                    if (response != null && Helper.isAudioFile(response))
                        callBack.onRequestIsSuccess(GeminiResponse(description = response))
                    else
                        callBack.onRequestIsFailure(response)
                }
            }
        }catch (e:Exception){
            callBack.onRequestIsFailure(e.message.toString())
            Log.e("askAi",e.message.toString())
        }
    }

